// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract FarmSwap {
    address public nootToken; // NOOT token address

    // Event emitted when NOOT is deposited for Farm Coins
    event NOOTDeposited(address indexed player, uint256 amount);

    constructor(address _nootToken) {
        nootToken = _nootToken; // Set NOOT token address during deployment
    }

    // For testing: Allows players to claim NOOT tokens after deducting Farm Coins off-chain
    function claimTestNOOT(uint256 amount) external {
        require(IERC20(nootToken).balanceOf(address(this)) >= amount, "Insufficient NOOT reserve");
        IERC20(nootToken).transfer(msg.sender, amount);
    }

    // Swap NOOT tokens for Farm Coins
    function swapNOOTForFarmCoins(uint256 nootAmount) external {
        IERC20(nootToken).transferFrom(msg.sender, address(this), nootAmount);
        emit NOOTDeposited(msg.sender, nootAmount);
    }

    // Optional: Function to fund the contract with NOOT tokens
    function fundNOOT(uint256 amount) external {
        IERC20(nootToken).transferFrom(msg.sender, address(this), amount);
    }
}

