// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title FarmToken
 * @dev A farm-themed token for the Nooter's Farm game
 */
contract FarmToken is ERC20, Ownable {
    string private _symbol;
    string private _name;
    uint8 private _decimals;
    
    // Optional: token image URL stored on-chain
    string public tokenImageURL;
    
    // Events for token customization
    event TokenImageUpdated(string newImageURL);
    
    /**
     * @dev Constructor
     * @param name_ Token name
     * @param symbol_ Token symbol
     * @param initialSupply Initial supply of tokens
     * @param tokenImage URL to token image (can be empty)
     */
    constructor(
        string memory name_,
        string memory symbol_,
        uint256 initialSupply,
        string memory tokenImage
    ) ERC20(name_, symbol_) Ownable(msg.sender) {
        _name = name_;
        _symbol = symbol_;
        _decimals = 18;
        tokenImageURL = tokenImage;
        
        // Mint initial supply to deployer
        _mint(msg.sender, initialSupply * 10 ** decimals());
    }
    
    /**
     * @dev Update the token image URL
     * @param newImageURL New image URL
     */
    function updateTokenImage(string memory newImageURL) external onlyOwner {
        tokenImageURL = newImageURL;
        emit TokenImageUpdated(newImageURL);
    }
    
    /**
     * @dev Mint new tokens (only owner)
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }
    
    /**
     * @dev Burn tokens from sender
     * @param amount Amount to burn
     */
    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }
    
    /**
     * @dev Burn tokens from a specific account (must have allowance)
     * @param account Address to burn from
     * @param amount Amount to burn
     */
    function burnFrom(address account, uint256 amount) external {
        _spendAllowance(account, msg.sender, amount);
        _burn(account, amount);
    }
}

