// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    uint public unlockTime;
    address payable public owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        // Replace transfer with call
        (bool success, ) = owner.call{value: address(this).balance}("");
        require(success, "Transfer failed.");
    }
}

contracts/MultiFarmSwap.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MultiFarmSwap
 * @dev A contract for swapping multiple tokens for Farm Coins in the Nooter's Farm game
 */
contract MultiFarmSwap is Ownable {
    // Token information structure
    struct TokenInfo {
        bool isSupported;      // Whether the token is supported
        uint256 exchangeRate;  // How many Farm Coins per token (multiplied by 10)
        uint256 balance;       // Current token balance for tracking purposes
    }
    
    // Mapping of token addresses to their info
    mapping(address => TokenInfo) public supportedTokens;
    
    // List of supported token addresses for enumeration
    address[] public tokenList;
    
    // Events
    event TokenAdded(address indexed tokenAddress, uint256 exchangeRate);
    event TokenRemoved(address indexed tokenAddress);
    event TokenSwapped(address indexed tokenAddress, address indexed player, uint256 tokenAmount, uint256 farmCoinsAmount);
    event TokenFunded(address indexed tokenAddress, address indexed funder, uint256 amount);
    event TokenClaimed(address indexed tokenAddress, address indexed player, uint256 amount);
    event ExchangeRateUpdated(address indexed tokenAddress, uint256 oldRate, uint256 newRate);
    
    /**
     * @dev Constructor - initializes with NOOT as the first supported token
     * @param _initialToken The address of the initial token (typically NOOT)
     */
    constructor(address _initialToken) Ownable(msg.sender) {
        // Add the initial token with default exchange rate of 10 Farm Coins per token
        _addToken(_initialToken, 100); // 100 means 10.0 Farm Coins per token (multiplied by 10)
    }
    
    /**
     * @dev Add a new token to be supported by the swap
     * @param tokenAddress The address of the token to add
     * @param exchangeRate How many Farm Coins per token (multiplied by 10 for precision)
     */
    function addToken(address tokenAddress, uint256 exchangeRate) external onlyOwner {
        _addToken(tokenAddress, exchangeRate);
    }
    
    /**
     * @dev Internal function to add a token
     */
    function _addToken(address tokenAddress, uint256 exchangeRate) internal {
        require(tokenAddress != address(0), "Invalid token address");
        require(!supportedTokens[tokenAddress].isSupported, "Token already supported");
        require(exchangeRate > 0, "Exchange rate must be positive");
        
        supportedTokens[tokenAddress] = TokenInfo({
            isSupported: true,
            exchangeRate: exchangeRate,
            balance: 0
        });
        
        tokenList.push(tokenAddress);
        
        emit TokenAdded(tokenAddress, exchangeRate);
    }
    
    /**
     * @dev Remove a token from being supported
     * @param tokenAddress The address of the token to remove
     */
    function removeToken(address tokenAddress) external onlyOwner {
        require(supportedTokens[tokenAddress].isSupported, "Token not supported");
        
        // Find and remove token from the list
        for (uint256 i = 0; i < tokenList.length; i++) {
            if (tokenList[i] == tokenAddress) {
                tokenList[i] = tokenList[tokenList.length - 1];
                tokenList.pop();
                break;
            }
        }
        
        // Mark as not supported but keep the balance info
        supportedTokens[tokenAddress].isSupported = false;
        
        emit TokenRemoved(tokenAddress);
    }
    
    /**
     * @dev Update the exchange rate for a token
     * @param tokenAddress The address of the token
     * @param newExchangeRate The new exchange rate (multiplied by 10)
     */
    function updateExchangeRate(address tokenAddress, uint256 newExchangeRate) external onlyOwner {
        require(supportedTokens[tokenAddress].isSupported, "Token not supported");
        require(newExchangeRate > 0, "Exchange rate must be positive");
        
        uint256 oldRate = supportedTokens[tokenAddress].exchangeRate;
        supportedTokens[tokenAddress].exchangeRate = newExchangeRate;
        
        emit ExchangeRateUpdated(tokenAddress, oldRate, newExchangeRate);
    }
    
    /**
     * @dev Swap tokens for Farm Coins
     * @param tokenAddress The address of the token to swap
     * @param tokenAmount The amount of tokens to swap
     */
    function swapTokenForFarmCoins(address tokenAddress, uint256 tokenAmount) external {
        require(supportedTokens[tokenAddress].isSupported, "Token not supported");
        require(tokenAmount > 0, "Amount must be positive");
        
        IERC20 token = IERC20(tokenAddress);
        require(token.transferFrom(msg.sender, address(this), tokenAmount), "Transfer failed");
        
        // Update the balance in our tracking
        supportedTokens[tokenAddress].balance += tokenAmount;
        
        // Calculate Farm Coins to give (token amount * exchange rate / 10)
        uint256 farmCoinsAmount = (tokenAmount * supportedTokens[tokenAddress].exchangeRate) / 10;
        
        emit TokenSwapped(tokenAddress, msg.sender, tokenAmount, farmCoinsAmount);
    }
    
    /**
     * @dev For testing: Allows players to claim tokens (reducing Farm Coins off-chain)
     * @param tokenAddress The address of the token to claim
     * @param tokenAmount The amount of tokens to claim
     */
    function claimTestTokens(address tokenAddress, uint256 tokenAmount) external {
        require(supportedTokens[tokenAddress].isSupported, "Token not supported");
        require(tokenAmount > 0, "Amount must be positive");
        
        IERC20 token = IERC20(tokenAddress);
        uint256 contractBalance = token.balanceOf(address(this));
        require(contractBalance >= tokenAmount, "Insufficient token reserve");
        
        require(token.transfer(msg.sender, tokenAmount), "Transfer failed");
        
        // Update the balance in our tracking
        if (supportedTokens[tokenAddress].balance >= tokenAmount) {
            supportedTokens[tokenAddress].balance -= tokenAmount;
        } else {
            supportedTokens[tokenAddress].balance = 0;
        }
        
        // Calculate Farm Coins equivalent (token amount * exchange rate / 10)
        uint256 farmCoinsAmount = (tokenAmount * supportedTokens[tokenAddress].exchangeRate) / 10;
        
        emit TokenClaimed(tokenAddress, msg.sender, tokenAmount);
    }
    
    /**
     * @dev Fund the contract with tokens
     * @param tokenAddress The address of the token to fund
     * @param amount The amount of tokens to fund
     */
    function fundToken(address tokenAddress, uint256 amount) external {
        require(supportedTokens[tokenAddress].isSupported, "Token not supported");
        require(amount > 0, "Amount must be positive");
        
        IERC20 token = IERC20(tokenAddress);
        require(token.transferFrom(msg.sender, address(this), amount), "Transfer failed");
        
        // Update the balance in our tracking
        supportedTokens[tokenAddress].balance += amount;
        
        emit TokenFunded(tokenAddress, msg.sender, amount);
    }
    
    /**
     * @dev Get the list of all supported tokens
     * @return An array of token addresses
     */
    function getAllSupportedTokens() external view returns (address[] memory) {
        return tokenList;
    }
    
    /**
     * @dev Get token information
     * @param tokenAddress The address of the token
     * @return isSupported Whether the token is supported
     * @return exchangeRate The exchange rate for the token
     * @return balance The current balance of the token in the contract
     * @return actualBalance The actual balance of the token in the contract (from the token contract)
     */
    function getTokenInfo(address tokenAddress) external view returns (
        bool isSupported,
        uint256 exchangeRate,
        uint256 balance,
        uint256 actualBalance
    ) {
        TokenInfo storage info = supportedTokens[tokenAddress];
        return (
            info.isSupported,
            info.exchangeRate,
            info.balance,
            IERC20(tokenAddress).balanceOf(address(this))
        );
    }
    
    /**
     * @dev Withdraw tokens in case of emergency or migration (owner only)
     * @param tokenAddress The address of the token to withdraw
     * @param amount The amount to withdraw
     * @param recipient The address to send the tokens to
     */
    function emergencyWithdraw(address tokenAddress, uint256 amount, address recipient) external onlyOwner {
        require(recipient != address(0), "Invalid recipient");
        
        IERC20 token = IERC20(tokenAddress);
        uint256 contractBalance = token.balanceOf(address(this));
        require(contractBalance >= amount, "Insufficient balance");
        
        require(token.transfer(recipient, amount), "Transfer failed");
        
        // Update the balance in our tracking if it's a supported token
        if (supportedTokens[tokenAddress].isSupported && supportedTokens[tokenAddress].balance >= amount) {
            supportedTokens[tokenAddress].balance -= amount;
        }
    }
}

